// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

import "./Token.sol";
import "./Covens.sol";
import "./Wizards.sol";
import "./Scepters.sol";
import "./Elixir.sol";

contract RewardPool is 
    AccessControlEnumerable 
{
    mapping(address => uint256) private _balanceLocked;
    mapping(address => uint256) private _latestClaim;
    uint256 private nfts;

    bytes32 public constant TRANSFER_ROLE = keccak256("TRANSFER_ROLE");
    bytes32 public constant MAIN_GAME = keccak256("MAIN_GAME");

    BEP20 token;
    ELIXIR elixir;
    Scepters scepters;
    Covens covens;
    Wizards wizards;

    constructor(
        BEP20 _token,
        ELIXIR _elixir,
        Covens _covens,
        Wizards _wizards,
        Scepters _scepters
    ) {
        elixir = _elixir;
        token = _token;
        scepters = _scepters;
        covens = _covens;
        wizards = _wizards;

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(TRANSFER_ROLE, _msgSender());
        _setupRole(MAIN_GAME, _msgSender());
    }

    function transferORKL(address recipient, uint256 amount) public returns(bool success) {
        require(hasRole(TRANSFER_ROLE, _msgSender()));
        token.transfer(recipient, amount);
        return true;
    }

    function transferELIXIR(address recipient, uint256 amount) public returns(bool success) {
        require(hasRole(TRANSFER_ROLE, _msgSender()));
        elixir.transfer(recipient, amount);
        return true;
    }

    function getBalanceLocked(address from) public view returns(uint256) {
        return _balanceLocked[from];
    }

    function setBalanceLocked(address from, uint256 amount) public returns(bool) {
        require(hasRole(MAIN_GAME, _msgSender()));
        _balanceLocked[from] = amount;
        return true;
    }

    function setLatestClaim(address from, uint256 amount) public {
        require(hasRole(MAIN_GAME, _msgSender()));
        _latestClaim[from] = amount;
    }

    function getLatestClaim(address from) public view returns(uint256) {
        return _latestClaim[from];
    }

    function transferORKLFrom(address from, address recipient, uint256 amount) public {
        require(hasRole(MAIN_GAME, _msgSender()));
        token.transferFrom(from, recipient, amount);
    }

    function transferELIXIRFrom(address from, address recipient, uint256 amount) public {
        require(hasRole(MAIN_GAME, _msgSender()));
        elixir.transferFrom(from, recipient, amount);
    }

    function burnELIXIR() public {
        require(hasRole(MAIN_GAME, _msgSender()));
        elixir.burn(elixir.balanceOf(address(this)));
    }

    function transferWizardFrom(address from, address recipient, uint256 wizardId) public {
        require(hasRole(MAIN_GAME, _msgSender()));
        wizards.transferFrom(from, recipient, wizardId);
    }

    function transferCovenFrom(address from, address recipient, uint256 covenId) public {
        require(hasRole(MAIN_GAME, _msgSender()));
        covens.transferFrom(from, recipient, covenId);
    }

    function transferScepterFrom(address from, address recipient, uint256 scepterId) public {
        require(hasRole(MAIN_GAME, _msgSender()));
        scepters.transferFrom(from, recipient, scepterId);
    }

    function getNFTs() public view returns(uint256) {
        return nfts;
    }

    function setNFTs(uint256 amount) public {
        require(hasRole(MAIN_GAME, _msgSender()));
        nfts = amount;
    }

    function burnWizard(uint256 wizardId) public {
        require(hasRole(MAIN_GAME, _msgSender()));
        wizards.burn(wizardId);
    }

    function burnScepter(uint256 scepterId) public {
        require(hasRole(MAIN_GAME, _msgSender()));
        scepters.burn(scepterId);
    }

    function mintElixir(address receiver, uint256 amount) public {
        require(hasRole(MAIN_GAME, _msgSender()));
        elixir.mint(receiver, amount);
    }
}
